function B64toImg($baseFromChec)
{
    //$baseFromChec = "data:image/png;base64,";
    $data = base64_decode(preg_replace('#^data:image/\w+;base64,#i', '', $baseFromChec));
    $filepath = "copiaFactura/codBarras.png";
    file_put_contents($filepath, $data);


